import java.util.*;
public class Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		System.out.print("Enter a Number: ");
		int num = sc.nextInt();
		if (num/2==0)
		{
			System.out.println(num+" is a even number");
		}
		else {
			System.out.println(num+" is a even number");
		}

	}

}
